package lux.pe.na.v8reference_method_advanced.functions;public class NumberUtils {    private NumberUtils() {    }    public static boolean prime(int value) {        for (int i = 2; i < value; i++) {            if (value % i == 0) {                return false;            }        }        return true;    }    public static int squared(int value) {        return value * value;    }}